import React, { useState } from "react";
import {
  Label,
  Input,
  FormGroup,
  Button,
  Card,
  CardHeader,
  CardBody,
} from "reactstrap";
import { useSelector, useDispatch } from "react-redux";
import { Redirect, Link } from "react-router-dom";

import "./Auth.css";
import Navigation from "../Navigation";
import { SignIn } from "../../store/modules/auth/actions/authAction";

const Login = () => {
  const currentState = useSelector((state) => state.Auth);

  const [user, setUser] = useState({
    email: "",
    password: "",
  });
  //const [email, setEmail] = useState()
  //const [password, setPassword] = useState()
  const dispatch = useDispatch();

  const userLogin = (credentials) => dispatch(SignIn(credentials));
  /*
  const handleEmailChange = e =>{
    setEmail(e.target.value)
  }
  const handlePasswordChange = e =>{
    setPassword(e.target.value)
  }
*/
  const handleChange = (e) => {
    setUser({
      ...user,
      [e.target.name]: e.target.value,
    });
  };
  const submitUser = (e) => {
    e.preventDefault();
    /*dispatch(SignIn({
      email, password
    }))*/
    userLogin({
      email: user.email,
      password: user.password,
    });
  };

  if (currentState.isAuthenticated) {
    return <Redirect to="/" />;
  }

  return (
    <div className="App">
      <div>
        <Navigation />
      </div>
      <div className="container Auth">
        <Card className="card-style">
          <CardHeader>Login</CardHeader>
          <CardBody>
            <form onSubmit={submitUser}>
              <div className="mb-2">
                {currentState.loginError &&
                currentState.loginError.Incorrect_details ? (
                  <small className="color-red">
                    {currentState.loginError.Incorrect_details}
                  </small>
                ) : (
                  ""
                )}
                {currentState.loginError &&
                currentState.loginError.No_record ? (
                  <small className="color-red">
                    {currentState.loginError.No_record}
                  </small>
                ) : (
                  ""
                )}
              </div>
              <FormGroup>
                <Label>Email</Label>
                <Input
                  type="email"
                  name="email"
                  placeholder="Enter email"
                  onChange={handleChange}
                />
                {currentState.loginError &&
                currentState.loginError.Required_email ? (
                  <small className="color-red">
                    {currentState.loginError.Required_email}
                  </small>
                ) : (
                  ""
                )}
                {currentState.loginError &&
                currentState.loginError.Invalid_email ? (
                  <small className="color-red">
                    {currentState.loginError.Invalid_email}
                  </small>
                ) : (
                  ""
                )}
              </FormGroup>
              <FormGroup>
                <Label>Password</Label>
                <Input
                  type="password"
                  name="password"
                  placeholder="Enter password"
                  onChange={handleChange}
                />
                {currentState.loginError &&
                currentState.loginError.Required_password ? (
                  <small className="color-red">
                    {currentState.loginError.Required_password}
                  </small>
                ) : (
                  ""
                )}
                {currentState.loginError &&
                currentState.loginError.Invalid_password ? (
                  <small className="color-red">
                    {currentState.loginError.Invalid_password}
                  </small>
                ) : (
                  ""
                )}
                {currentState.loginError &&
                currentState.loginError.Incorrect_password ? (
                  <small className="color-red">
                    {currentState.loginError.Incorrect_password}
                  </small>
                ) : (
                  ""
                )}
              </FormGroup>

              {currentState.isLoading ? (
                <Button color="primary" type="submit" block disabled>
                  Login...
                </Button>
              ) : (
                <Button
                  color="primary"
                  type="submit"
                  block
                  disabled={user.email === "" || user.password === ""}
                >
                  Login
                </Button>
              )}
            </form>
            <div
              className="mt-2"
              style={{ display: "flex", justifyContent: "space-between" }}
            >
              <div>
                <small>If you don't have profile: </small>
                <a>
                  <Link to="/signup">Sign Up</Link>
                </a>
              </div>
            </div>
          </CardBody>
        </Card>
      </div>
    </div>
  );
};

export default Login;
