import React, { useState } from 'react';
import { NavLink, Link } from 'react-router-dom';
import { useSelector, useDispatch } from "react-redux";

import { SignOut } from '../store/modules/auth/actions/authAction';
import './Navigation.css'

import {
    Collapse,
    Navbar,
    NavbarToggler,
    NavbarBrand,
    Nav,
    NavItem,
    UncontrolledDropdown,
    DropdownToggle,
    DropdownMenu,
    DropdownItem } from 'reactstrap';


const Navigation = () => {
  if (localStorage.getItem('user_token') != ""){
  const authToken = localStorage.getItem('user_token')
  const base64Url = authToken.split('.')[1];
  const base64 = base64Url.replace('-', '+').replace('_', '/');
  const userId = JSON.parse(window.atob(base64));
  const user_id = userId.user_id
  }

  const [isOpen, setIsOpen] = useState(false)

  const currentState = useSelector((state) => state);
  
  const { isAuthenticated, currentUser } = currentState.Auth;

  const dispatch = useDispatch()

  const logoutUser  = () => dispatch(SignOut());

  const logout = (e) => {
    e.preventDefault()
    logoutUser()
  }

  const userProfile = isAuthenticated ?  `/profile/${1}` : ""

  const SignedInLinks = (
              <React.Fragment>
                  <NavItem className="mt-2" style={{marginRight: "15px" }}>
                    <NavLink to="/createquestion">Create question</NavLink>
                  </NavItem>
                  <NavItem className="mt-2" style={{marginRight: "15px" }}>
                    <NavLink to="/authquestions">My questions</NavLink>
                  </NavItem>
                  <NavItem className="mt-2" style={{marginRight: "15px" }}>
                    <NavLink to={userProfile}>Profile</NavLink>
                  </NavItem>
                  <NavItem className="mt-2" style={{marginRight: "15px" }}>
                    <a onClick={logout}>Logout</a>
                  </NavItem>
                  <UncontrolledDropdown nav inNavbar>
                  <DropdownMenu right>
                    <DropdownItem>
                      <NavItem>
                        <NavLink to={userProfile}>Profile</NavLink>
                      </NavItem>
                    </DropdownItem>
                    <DropdownItem divider />
                    <DropdownItem>
                      <a onClick={logout}>Logout</a>
                    </DropdownItem>
                  </DropdownMenu>
                </UncontrolledDropdown>
              </React.Fragment>

            )

  const SignedOutLinks = (
                <React.Fragment>
                  <NavItem style={{marginRight: "20px" }}>
                    <Link to='/login'>Login</Link>
                  </NavItem>
                  <NavItem>
                    <Link to='/signup'>Signup</Link>
                  </NavItem>
                </React.Fragment>
              )


  return (
    <div className="mb-3">
      <Navbar color="light" light expand="md"> 
          <NavbarBrand className="mx-auto" href="/">Ask.it</NavbarBrand>
          <NavbarToggler onClick={() => setIsOpen(!isOpen) } /> 
        <Collapse isOpen={isOpen} navbar> 
          <Nav className="ml-auto" navbar>
            { isAuthenticated ? SignedInLinks: SignedOutLinks }
          </Nav>
        </Collapse>
      </Navbar>
    </div>
  );
}

export default Navigation
