import React, { Fragment, useState, useEffect } from 'react'
import { useSelector, useDispatch } from "react-redux";
import { Redirect, NavLink } from 'react-router-dom';
import { Label, Input, FormGroup, Button, CardBody, Col, Row, Form, CustomInput, Modal, ModalHeader, ModalFooter, ModalBody } from "reactstrap";
import { updateUser, deleteUser, getUser } from '../../store/modules/auth/actions/authAction';
import  './Profile.css'
import Message from '../utils/Message';
import axios from "axios";

import Navigation from "../Navigation"

const Update = () => {
    const currentUserState = useSelector((state) => state.Auth);
    const dispatch = useDispatch()

    const userUpdate = (userDetails) => dispatch(updateUser(userDetails))

    const user_data = localStorage.getItem('user_info')
    const user_info = JSON.parse(user_data)
    
    const [user, setUser] = useState({
        email: user_info.email,
        name: user_info.name,
        sur_name: user_info.sur_name,
        id: user_info.id,

      })

    const submitUser = (e) => {
        e.preventDefault()
        userUpdate({
          email: user.email,
          name: user.name,
          sur_name: user.sur_name,
          id: user_info.id
        })
      }
    const handleChange = e => {
     setUser({
         ...user,
          [e.target.name]: e.target.value
        })
    }

    if(!currentUserState.isAuthenticated){
        return <Redirect to='/login' />
      }else {
        axios.defaults.headers.common["Authorization"]=`Bearer ${localStorage.getItem('user_token')}`
      }

    return(
        <Fragment>
        <Navigation />
        <Form onSubmit={submitUser}>
          <Row>
            <Col sm="12" md={{ size: 10, offset: 1 }}>
              <FormGroup>
                <Label for="exampleAddress">Email</Label>
                <Input type="email" name="email" value={user.email}  />
                { currentUserState.userError && currentUserState.userError.Required_email ? (
                  <small className="color-red">{currentUserState.userError.Required_email}</small>) : ("")
                }
                { currentUserState.userError && currentUserState.userError.Invalid_email ? (
                  <small className="color-red">{ currentUserState.userError.Invalid_email }</small>) : ("")
                }
                { currentUserState.userError && currentUserState.userError.Taken_email ? (
                  <small className="color-red">{ currentUserState.userError.Taken_email }</small>) : ("")
                }
              </FormGroup>
            </Col>
          </Row>
          <Row>
            <Col sm="12" md={{ size: 10, offset: 1 }}>
              <FormGroup>
                <Label for="exampleAddress">Name</Label>
                <Input type="text" name="name" value={user.name}   />
              </FormGroup>
            </Col>
          </Row>
          <Row>
            <Col sm="12" md={{ size: 10, offset: 1 }}>
              <FormGroup>
                <Label for="exampleAddress">Surname</Label>
                <Input type="text" name="sur_name" value={user.sur_name} />
              </FormGroup>
            </Col>
          </Row>
          <Row className="mt-4">
            <Col sm="12" md={{ size: 10, offset: 1 }}>
              <FormGroup>
                { currentUserState.authSuccessUser != null && currentUserState.userError == null ? (
                  <Message msg={currentUserState.authSuccessUser} />) : (""
                )}
              </FormGroup>
            </Col>
          </Row>
          <Row className="mt-3">
            <Col sm="12" md={{ size: 10, offset: 1 }}>
              <FormGroup>
                { currentUserState.isUpdatingUser ? (
                  <Button
                    color="primary"
                    type="submit"
                    block
                    disabled
                  >
                    Updating...
                </Button>
                ) : (
                  <Button
                    color="primary"
                    type="submit"
                    block
                  >
                  Update
                </Button>
                )}
              </FormGroup>
            </Col>
          </Row>
        </Form>
        </Fragment>
    )
}
export default Update
