import API_ROUTE from "../../../../apiRoute";
import axios from 'axios'
import setAuthorizationToken  from "../../../../authorization/authorization";
import { BEFORE_STATE, SIGNUP_SUCCESS, SIGNUP_ERROR, LOGIN_SUCCESS, LOGIN_ERROR, 
  LOGOUT_SUCCESS, UPDATE_USER_SUCCESS, UPDATE_USER_ERROR, BEFORE_USER_STATE, 
  DELETE_USER_SUCCESS, DELETE_USER_ERROR, FETCH_USERS, FETCH_USERS_ERROR } from '../authTypes'
import  {history} from '../../../../history'


export const SignIn = (credentials) => {
  return async (dispatch) => {
      dispatch({ type: BEFORE_STATE }) 
    try {
      const res = await axios.post(`${API_ROUTE}/login`, credentials)
      let userData = res.data
      localStorage.setItem("token", userData.token)
      localStorage.setItem('user_data', JSON.stringify(userData));
      var falseToken = localStorage.getItem('user_data')
      var trueToken = falseToken.replace(/"/g,'')
      window.localStorage.setItem('user_token',trueToken)
      setAuthorizationToken(trueToken)
      
      dispatch({ type: LOGIN_SUCCESS, payload: userData })
    } catch(err) {
      dispatch({ type: LOGIN_ERROR, payload: err.response.data.error })
    }
  }
}

export const SignOut = () => {
  return (dispatch) => {
    localStorage.removeItem("token")
    setAuthorizationToken(false)
    dispatch({ type: LOGOUT_SUCCESS })
    window.localStorage.clear(); //update the localstorage
    history.push('/login');
  }
}

export const SignUp = (newUser) => {
    return async (dispatch) => {
        dispatch({ type: BEFORE_STATE }) 
      try {
        await axios.post(`${API_ROUTE}/users`, newUser);
        dispatch({ type: SIGNUP_SUCCESS })
        history.push('/login');
      } catch(err) {
        dispatch({ type: SIGNUP_ERROR, payload: err.response.data.error })
    }
  }
}

export const updateUser = (updateUser) => {

  return async (dispatch, getState) => {
    dispatch({ type: BEFORE_USER_STATE })
    
    try {
      const res = await axios.put(`${API_ROUTE}/users/${updateUser.id}`, updateUser);
      let updatedUser = res.data

      dispatch({ type: UPDATE_USER_SUCCESS, payload: updatedUser })
      window.localStorage.setItem('user_data', JSON.stringify(updatedUser)); //update the localstorages
    } catch (err) {
      dispatch({ type: UPDATE_USER_ERROR, payload: err.response.data.error })
    }
  }
}

export const deleteUser = (id)  => {

  return async dispatch => {
    dispatch({ type: BEFORE_STATE })
    try {
      const res = await axios.delete(`${API_ROUTE}/users/${id}`);
      let deleteMessage = res.data.response
      dispatch({ type: DELETE_USER_SUCCESS, payload: deleteMessage })
      window.localStorage.clear(); //update the localstorage
      window.location.href = "/"
    } catch (err) {
      dispatch({ type: DELETE_USER_ERROR, payload: err.response.data.error })
    }
  }
}

export const getUser = (id) => {

  return async (dispatch) => {
    try{
      
      const res = await axios.get(`${API_ROUTE}/users/${id}`);
      let ja = res.data
      window.localStorage.setItem("user_info", JSON.stringify(ja))
      dispatch({ type: FETCH_USERS, payload: res.data })
    }catch (err) {
      dispatch({ type: FETCH_USERS_ERROR, payload: err ? err.data.error : "" })
    }
  }
}
