import React from 'react';
import ReactDOM from 'react-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import Routes from './Routes';
import * as serviceWorker from './serviceWorker';
import { Provider } from "react-redux"
import './index.css';
import store from './store/index'
import App from './App';
import reportWebVitals from './reportWebVitals';
import setAuthorizationToken  from './authorization/authorization';
import { LOGIN_SUCCESS } from './store/modules/auth/authTypes';
import { getUser } from './store/modules/auth/actions/authAction';
import { useDispatch } from "react-redux";

//when the page reloads, the auth user is still set
if (localStorage.getItem('user_token')){
  setAuthorizationToken(localStorage.getItem('user_token')) 
  const dispatch = useDispatch()
  const authToken = localStorage.getItem('user_token')
  const base64Url = authToken.split('.')[1];
  const base64 = base64Url.replace('-', '+').replace('_', '/');
  const userId = JSON.parse(window.atob(base64));
  const author_id = userId.user_id
  const getQuestions = (id) => dispatch(getUser(id));
    getQuestions(author_id);

  let userData = localStorage.getItem('user_info')== null ? null : JSON.parse(localStorage.getItem('user_info'))

  //let userData = localStorage.getItem('user_data') == null ? null : JSON.parse(localStorage.getItem('user_data'))
  store.dispatch({ type: LOGIN_SUCCESS, payload: userData}) //provided he has a valid token 

}
ReactDOM.render(<Provider store={store}><Routes /></Provider>, document.getElementById('root'));

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();

