import React, { Fragment, useState, useEffect } from 'react'
import { useSelector, useDispatch } from "react-redux";
import { Redirect, NavLink } from 'react-router-dom';
import { Label, Input, FormGroup, Button, CardBody, Col, Row, Form, CustomInput, Modal, ModalHeader, ModalFooter, ModalBody } from "reactstrap";
import { updateUser, deleteUser, getUser } from '../../store/modules/auth/actions/authAction';
import  './Profile.css'
import Message from '../utils/Message';



import Navigation from "../Navigation"

const Profile = () => {

  const authToken = localStorage.getItem('user_token')
  const base64Url = authToken.split('.')[1];
  const base64 = base64Url.replace('-', '+').replace('_', '/');
  const userId = JSON.parse(window.atob(base64));
  const author_id = userId.user_id

  const [modal, setModal] = useState(false);

  const toggle = (e) => {
    setModal(!modal);
  } 
  const dispatch = useDispatch()

  const currentUserState = useSelector((state) => state.Auth);
  
  const AuthID = currentUserState.currentUser ? author_id : ""
  
  //const getkenao = (id) => dispatch(getUser(id));
  useEffect(() => {
    //getUser(AuthID)
    dispatch(getUser(author_id))
  }, [])
  
  const user_data = localStorage.getItem('user_info')
  const user_info = JSON.parse(user_data)
  console.log(user_info)

  const deleteAccount = id => dispatch(deleteUser(id))

  //incase someone visits the route manually
  if(!currentUserState.isAuthenticated){
    return <Redirect to='/login' />
  }

  const shutDown = (e) => {
    e.preventDefault()
    deleteAccount(AuthID)
  }

  return (
    <Fragment>
      <Navigation />
      <div className="post-style container">
        <div className="card-style">
          <div className="text-center">
            <h4>Profile</h4>
          </div>
          <CardBody>
        <Row>
          <Col sm="12" md={{ size: 10, offset: 1 }}>
          {user_info? (
          <div style={{margin: "10px 0px 10px"}}>Name:<strong>{user_info.name}</strong></div>):("")}
          </Col>
          <Col sm="12" md={{ size: 10, offset: 1 }}>
          <div style={{margin: "10px 0px 10px"}}>Surname: <strong>{user_info.sur_name}</strong></div>
          </Col>
          <Col sm="12" md={{ size: 10, offset: 1 }}>
          <div style={{margin: "10px 0px 10px"}}>Email: <strong>{user_info.email}</strong></div>
          </Col>
        </Row>
        <Row className="mt-3">
          <Col sm="12" md={{ size: 10, offset: 1 }}>
            <FormGroup>
              <NavLink to="../update">
                <Button color="primary" block>Edit profile</Button>
              </NavLink>
            </FormGroup>
          </Col>
        </Row>
        <Row className="mt-3">
          <Col sm="12" md={{ size: 10, offset: 1 }}>
            <FormGroup>
              <Button onClick={toggle} color="danger" type="submit" block >
              Deactivate Account
              </Button>
            </FormGroup>
          </Col>
        </Row>
        </CardBody>

        <Modal isOpen={modal} toggle={toggle}>
        <ModalHeader toggle={toggle} className="text-center">Are you sure you want to delete your account?</ModalHeader>
        <ModalBody toggle={toggle} className="text-center">This will also delete your posts, likes and comments if you created any.</ModalBody>
        <ModalFooter>
        { currentUserState.isLoading ? (
              <button className="btn btn-danger"
                disabled
              >
                Deleting...
            </button>
            ) : (
              <button className="btn btn-danger"
                onClick={shutDown}
                type="submit"
              >
              Delete
            </button>
            )}
          <Button color="secondary" onClick={toggle}>Cancel</Button>
        </ModalFooter>
      </Modal>
      </div>
    </div>
  </Fragment>
  )
}

export default Profile
