import React, { useEffect } from 'react'
import { Link, Redirect } from 'react-router-dom'
import { useSelector, useDispatch } from "react-redux";
import { FaFilter } from 'react-icons/fa'

import { fetchAuthQuestions } from '../../store/modules/questions/actions/questionsAction';
import AuthQuestion from './AuthQuestion'
import Navigation from '../Navigation'
import './Question.css';
import axios from "axios";


const AuthQuestions = () => {

  const currentState = useSelector((state) => state.Auth);

  const authToken = localStorage.getItem('user_token')
  const base64Url = authToken.split('.')[1];
  const base64 = base64Url.replace('-', '+').replace('_', '/');
  const userId = JSON.parse(window.atob(base64));
  const author_id = userId.user_id
  console.log(author_id)
  const questionsSelector = useSelector((state) => state.QuestionsState);
  const dispatch = useDispatch();

  const getAuthQuestions = (id) => dispatch(fetchAuthQuestions(id));

  useEffect(() => {
    getAuthQuestions(author_id);
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [])

   //incase someone visits the route manually
  if(!currentState.isAuthenticated){
    return <Redirect to='/login' />
  }else{
    axios.defaults.headers.common["Authorization"]=`Bearer ${localStorage.getItem('user_token')}`
  }

  let authQuestions = questionsSelector.authQuestions.map(question => {
    console.log(question.id)
    return (
      <div  className="mt-2 style-card" key={question.id}>
       <Link to={'/questions/' + question.id} key={question.id}>
        <AuthQuestion question={question} key={question.id} />
      </Link>
    </div>
      );
  })
    

  return (
    <div className="App">
      <div>
        <Navigation />
      </div>
      <div className="container">
        { authQuestions.length > 0 ? (
          <div className="container">{authQuestions}</div>
        ) : ( 
          <div className="text-center mt-4">
            <div style={{fontSize: "100px"}}><FaFilter /></div>
            <p className="mt-2">It seems you have not created any Questions yet.</p>
            <p>Click the button the button below to create one</p>
            <div className="mt-4">
              <Link to="/createquestion" className="btn btn-primary">Create Question</Link>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default AuthQuestions
